package day1homework;

public final class LearningDataTypes {

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myName = "Govardhan";
		int age = 32;
		float height = 5.6f;
		String bloodGroup = "O+";
		char initial = 'S';
		boolean isMarried = true;
		long tnCorruptionValue = 1000000000;
		double indianCorruptionValue = 100000000000000000000000000000000000d;
		
		System.out.println("My Name is - " + myName);
		System.out.println("My Initial is - " + initial);
		System.out.println("My Age is - " + age);
		System.out.println("My height is - "+height);
		System.out.println("My Blood Group is - "+bloodGroup);
		System.out.println("Married - " + isMarried);
		System.out.println(tnCorruptionValue);
		System.out.println(indianCorruptionValue);
		 
		
	}

}
